mapper.insert:
INSERT INTO SYS_NOTI_INFO (
	NOTI_CODE
	, NOTI_NAME
	, NOTI_CONTENT
	, BSNS_CATE
	, NOTI_REG_DATE
	, NOTI_MOD_DATE
	, NOTI_MOD_USER
)
VALUES(
	sfUniqueKey()
	, #{noti_name}
	, #{noti_content}
	, #{bsns_cate}
	, NOW()
	, NOW()
	, #{user_id}
)

mapper.update:
UPDATE sys_noti_info
SET
	NOTI_NAME = #{noti_name}
  , NOTI_CONTENT = #{noti_content}
  , BSNS_CATE = #{bsns_cate}
  , NOTI_MOD_DATE = NOW()
  , NOTI_MOD_USER = #{user_id}
WHERE NOTI_CODE = #{noti_code}
