mapper.insert:
INSERT INTO AC_ROOM_INFO (
	COMP_CODE
	, BSNS_CODE
	, ROOM_CODE
	, ROOM_NAME
	, ROOM_LIMIT
	, ROOM_TEACHER_NAME
	, ROOM_SUB_TEACHER_NAME
	, ROOM_REG_DATE
	, ROOM_MOD_DATE
	, ROOM_MOD_USER
)
VALUES (
	#{comp_code}
	, #{bsns_code}
	, sfUniqueKey()
	, #{room_name}
	, #{room_limit}
	, #{room_teacher_name}
	, #{room_sub_teacher_name}
	, NOW()
	, NOW()
	, #{user_id}
)

mapper.update:
UPDATE AC_ROOM_INFO
SET
	ROOM_NAME = #{room_name}
  , ROOM_LIMIT = #{room_limit}
  , ROOM_TEACHER_NAME = #{room_teacher_name}
  , ROOM_SUB_TEACHER_NAME = #{room_sub_teacher_name}
  , ROOM_MOD_DATE = NOW()
  , ROOM_MOD_USER = #{user_id}
WHERE COMP_CODE = #{comp_code}
  AND BSNS_CODE = #{bsns_code}
  AND ROOM_CODE = #{room_code}
