mapper.insert:
INSERT INTO SYS_INDEX_INFO (
	 INDEX_TYPE
	,INDEX_DATE
	,INDEX_NUMB
	,INDEX_REG_DATE
	,INDEX_MOD_DATE
	,INDEX_MOD_USER
)
VALUES (
	 #{index_type}
	,#{index_date}
	,'1'
	,NOW()
	,NOW()
	,#{user_id}
)
ON DUPLICATE KEY UPDATE
	 INDEX_NUMB = INDEX_NUMB + 1
	,INDEX_MOD_DATE = NOW()
	,INDEX_MOD_USER = #{user_id}


	SELECT INDEX_NUMB
	FROM SYS_INDEX_INFO
	WHERE INDEX_TYPE = #{index_type}
		AND INDEX_DATE = #{index_date}

mapper.select:
SELECT DMI.MENU_LEVEL , DMI.HIGH_MENU_CODE , DMI.MENU_CODE , DPI.PAGE_NAME AS MENU_NAME , DPI.PAGE_URL AS MENU_URL , DPI.PAGE_ICON AS MENU_ICON , DMI.MENU_DEPTH , DMI.MENU_SEQ , DPI.PAGE_YN AS MENU_YN , IF( (SELECT COUNT(HIGH_MENU_CODE) FROM DF_MENU_INFO WHERE HIGH_MENU_CODE = DMI.MENU_CODE ) > 0, TRUE, FALSE ) AS HAS_CHILD FROM DF_MENU_INFO DMI JOIN DF_PAGE_INFO DPI ON DPI.PAGE_CODE = DMI.MENU_CODE WHERE PAGE_YN = 'Y'
AND DMI.MENU_CODE = #{menu_code}
AND HIGH_MENU_CODE = #{high_menu_code}
AND MENU_LEVEL = #{user_level}
ORDER BY MENU_SEQ
