mapper.insert:
INSERT INTO EO_SUB_FACILITY
(
	FACILITY_TAG_NO
	, SUB_FACILITY_NO
	, SUB_FACILITY_NAME
	, SUB_FACILITY_SPEC
	, SUB_FACILITY_QUANTITY
	, SUB_FACILITY_PRODUCTION_COMPANY
	, SUB_FACILITY_NOTE
)
VALUES(
	#{facility_tag_no}
	, #{sub_facility_no}
	, #{sub_facility_name}
	, #{sub_facility_spec}
	, #{sub_facility_quantity}
	, #{sub_facility_production_company}
	, #{sub_facility_note}
)

mapper.update:
UPDATE EO_SUB_FACILITY
SET
	SUB_FACILITY_NAME = #{sub_facility_name}
  , SUB_FACILITY_SPEC = #{sub_facility_spec}
  , SUB_FACILITY_QUANTITY = #{sub_facility_quantity}
  , SUB_FACILITY_PRODUCTION_COMPANY = #{sub_facility_production_company}
  , SUB_FACILITY_NOTE = #{sub_facility_note}
WHERE SUB_FACILITY_NO = #{sub_facility_no}
