mapper.insert:
INSERT INTO SYS_CODE_INFO (
	CODE_ID
	, CODE_NAME
)
VALUES (
	#{code_id}
	, #{code_name}
)

mapper.select:
SELECT CODE_ID , CDDT_VAL , CDDT_NAME , CDDT_SEQ , DATE_FORMAT(CDDT_REG_DATE, '%Y-%m-%d') AS CDDT_REG_DATE , DATE_FORMAT(CDDT_MOD_DATE, '%Y-%m-%d') AS CDDT_MOD_DATE , CDDT_MOD_USER , (SELECT USER_NAME FROM DF_USER_INFO WHERE USER_ID = CDDT_MOD_USER ) AS CDDT_MOD_USER_NAME FROM SYS_CODE_DETAIL
AND CODE_ID = #{code_id}
AND CDDT_VAL = #{cddt_val}
AND CDDT_NAME = #{cddt_name}
ORDER BY CDDT_SEQ

mapper.insert:

	SELECT IFNULL(MAX(CDDT_SEQ) + 1, 1) AS CDDT_SEQ
	FROM SYS_CODE_DETAIL
	WHERE CODE_ID = #{code_id}


INSERT INTO SYS_CODE_DETAIL (
	CODE_ID
	, CDDT_VAL
	, CDDT_NAME
	, CDDT_SEQ
	, CDDT_REG_DATE
	, CDDT_MOD_DATE
	, CDDT_MOD_USER
)
VALUES(
	#{code_id}
	, #{cddt_val}
	, #{cddt_name}
	, #{cddt_seq}
	, NOW()
	, NOW()
	, #{user_id}
)

mapper.update:
UPDATE SYS_CODE_DETAIL
SET
	CDDT_SEQ = #{cddt_seq}
	, CDDT_MOD_DATE = NOW()
	, CDDT_MOD_USER = #{user_id}
WHERE CODE_ID = #{code_id}
	AND CDDT_VAL = #{cddt_val}
