mapper.select:
SELECT GHM_CODE , BSNS_CATE , (SELECT CDDT_NAME FROM SYS_CODE_DETAIL WHERE CDDT_VAL = BSNS_CATE ) AS BSNS_CATE_NAME , GHM_YEAR , GHM_ACTIVE_YN , IF(GHM_ACTIVE_YN='Y', '활성화', '비활성화') AS GHM_ACTIVE_YN_NAME , DATE_FORMAT(GHM_REG_DATE, '%Y-%m-%d') AS GHM_REG_DATE , DATE_FORMAT(GHM_MOD_DATE, '%Y-%m-%d') AS GHM_MOD_DATE , GHM_MOD_USER , (SELECT USER_NAME FROM DF_USER_INFO WHERE USER_ID = GHM_MOD_USER ) AS GHM_MOD_USER_NAME FROM SYS_GHM_INFO
AND GHM_ACTIVE_YN = #{ghm_active_yn}
AND BSNS_CATE = #{bsns_cate}
AND GHM_YEAR = #{ghm_year}

SELECT GHM_CODE , BSNS_CATE , (SELECT CDDT_NAME FROM SYS_CODE_DETAIL WHERE CDDT_VAL = BSNS_CATE ) AS BSNS_CATE_NAME , GHM_YEAR , GHM_ACTIVE_YN , IF(GHM_ACTIVE_YN='Y', '활성화', '비활성화') AS GHM_ACTIVE_YN_NAME , DATE_FORMAT(GHM_REG_DATE, '%Y-%m-%d') AS GHM_REG_DATE , DATE_FORMAT(GHM_MOD_DATE, '%Y-%m-%d') AS GHM_MOD_DATE , GHM_MOD_USER , (SELECT USER_NAME FROM DF_USER_INFO WHERE USER_ID = GHM_MOD_USER ) AS GHM_MOD_USER_NAME FROM SYS_GHM_INFO
AND GHM_CODE = #{ghm_code}
AND BSNS_CATE = #{bsns_cate}
AND GHM_YEAR = #{ghm_year}

mapper.insert:
INSERT INTO SYS_GHM_INFO (
	GHM_CODE
	, BSNS_CATE
	, GHM_YEAR
	, GHM_REG_DATE
	, GHM_MOD_DATE
	, GHM_MOD_USER
)
VALUES (
	sfUniqueKey()
	, #{bsns_cate}
	, #{ghm_year}
	, NOW()
	, NOW()
	, #{user_id}
)

mapper.select:
SELECT DISTINCT(GHM_YEAR) FROM SYS_GHM_INFO WHERE GHM_ACTIVE_YN = 'Y'
AND BSNS_CATE = #{bsns_cate}
ORDER BY GHM_YEAR DESC

SELECT GHM_CODE , GWAN_CODE , GWAN_IO_TYPE , IF(GWAN_IO_TYPE='I', '수입', '지출') AS GWAN_IO_TYPE_NAME , GWAN_NUMB , GWAN_NAME , GWAN_NOTE , IFNULL(HANG_CODE, '') AS HANG_CODE , IFNULL(HANG_NUMB, '') AS HANG_NUMB , IFNULL(HANG_NAME, '') AS HANG_NAME , IFNULL(HANG_NOTE, '') AS HANG_NOTE , IFNULL(MOCK_CODE, '') AS MOCK_CODE , IFNULL(MOCK_NUMB, '') AS MOCK_NUMB , IFNULL(MOCK_NAME, '') AS MOCK_NAME , IFNULL(MOCK_NOTE, '') AS MOCK_NOTE FROM SYS_GWAN_INFO SGI LEFT JOIN SYS_HANG_INFO SHI USING (GWAN_CODE) LEFT JOIN SYS_MOCK_INFO SMI USING (HANG_CODE) JOIN SYS_GHM_INFO USING (GHM_CODE)
AND GHM_CODE = #{ghm_code}
AND BSNS_CATE = #{bsns_cate}
AND GHM_YEAR = #{ghm_year}

mapper.update:
UPDATE SYS_GHM_INFO
SET
	GHM_ACTIVE_YN = 'Y'
  , GHM_MOD_DATE = NOW()
  , GHM_MOD_USER = #{user_id}
WHERE GHM_CODE = #{ghm_code}

mapper.select:
SELECT
	GHM_CODE
	, GWAN_CODE
	, GWAN_IO_TYPE
	, IF(GWAN_IO_TYPE='I', '수입', '지출') AS GWAN_IO_TYPE_NAME
	, GWAN_NUMB
	, GWAN_NAME
	, GWAN_NOTE
	, DATE_FORMAT(GWAN_REG_DATE, '%Y-%m-%d') AS GWAN_REG_DATE
	, DATE_FORMAT(GWAN_MOD_DATE, '%Y-%m-%d') AS GWAN_MOD_DATE
	, GWAN_MOD_USER
	, (SELECT USER_NAME
		FROM DF_USER_INFO
		WHERE USER_ID = GWAN_MOD_USER
	) AS GWAN_MOD_USER_NAME
FROM SYS_GWAN_INFO
	JOIN SYS_GHM_INFO USING (GHM_CODE)


		AND GWAN_CODE = #{gwan_code}


		AND GHM_CODE = #{ghm_code}


		AND BSNS_CATE = #{bsns_cate}


		AND GHM_YEAR = #{ghm_year}


		AND GWAN_IO_TYPE = #{gwan_io_type}


		AND GWAN_NUMB = #{gwan_numb}


		AND GHM_ACTIVE_YN = #{ghm_active_yn}

mapper.insert:
INSERT INTO SYS_GWAN_INFO (
	GHM_CODE
	, GWAN_CODE
	, GWAN_IO_TYPE
	, GWAN_NUMB
	, GWAN_NAME
	, GWAN_NOTE
	, GWAN_REG_DATE
	, GWAN_MOD_DATE
	, GWAN_MOD_USER
)
VALUES (
	#{ghm_code}
	, #{gwan_code}
	, #{gwan_io_type}
	, #{gwan_numb}
	, #{gwan_name}
	, #{gwan_note}
	, NOW()
	, NOW()
	, #{user_id}
)

mapper.update:
UPDATE SYS_GWAN_INFO
SET
	GWAN_NAME = #{gwan_name}
	, GWAN_NOTE = #{gwan_note}
	, GWAN_MOD_DATE = NOW()
	, GWAN_MOD_USER = #{user_id}
WHERE GWAN_CODE = #{gwan_code}

mapper.select:
SELECT GWAN_CODE , HANG_CODE , HANG_NUMB , HANG_NAME , HANG_NOTE , DATE_FORMAT(HANG_REG_DATE, '%Y-%m-%d') AS HANG_REG_DATE , DATE_FORMAT(HANG_MOD_DATE, '%Y-%m-%d') AS HANG_MOD_DATE , HANG_MOD_USER , (SELECT USER_NAME FROM DF_USER_INFO WHERE USER_ID = HANG_MOD_USER ) AS HANG_MOD_USER_NAME , GHM_CODE , GWAN_CODE , GWAN_IO_TYPE , IF(GWAN_IO_TYPE='I', '수입', '지출') AS GWAN_IO_TYPE_NAME , GWAN_NUMB , GWAN_NAME , GWAN_NOTE FROM SYS_HANG_INFO JOIN SYS_GWAN_INFO USING (GWAN_CODE)
AND HANG_CODE = #{hang_code}
AND GWAN_CODE = #{gwan_code}
AND HANG_NUMB = #{hang_numb}
AND GHM_CODE = #{ghm_code}

mapper.insert:
INSERT INTO SYS_HANG_INFO (
	GWAN_CODE
	, HANG_CODE
	, HANG_NUMB
	, HANG_NAME
	, HANG_NOTE
	, HANG_REG_DATE
	, HANG_MOD_DATE
	, HANG_MOD_USER
)
VALUES (
	#{gwan_code}
	, #{hang_code}
	, #{hang_numb}
	, #{hang_name}
	, #{hang_note}
	, NOW()
	, NOW()
	, #{user_id}
)

mapper.update:
UPDATE SYS_HANG_INFO
SET
	HANG_NAME = #{hang_name}
	, HANG_NOTE = #{hang_note}
	, HANG_MOD_DATE = NOW()
	, HANG_MOD_USER = #{user_id}
WHERE HANG_CODE = #{hang_code}

mapper.select:
SELECT HANG_CODE , MOCK_CODE , MOCK_NUMB , MOCK_NAME , MOCK_NOTE , HANG_CODE , HANG_NUMB , HANG_NAME , GWAN_CODE , GWAN_IO_TYPE , IF(GWAN_IO_TYPE='I', '수입', '지출') AS GWAN_IO_TYPE_NAME , GWAN_NUMB , GWAN_NAME , DATE_FORMAT(MOCK_REG_DATE, '%Y-%m-%d') AS MOCK_REG_DATE , DATE_FORMAT(MOCK_MOD_DATE, '%Y-%m-%d') AS MOCK_MOD_DATE , MOCK_MOD_USER , (SELECT USER_NAME FROM DF_USER_INFO WHERE USER_ID = MOCK_MOD_USER ) AS MOCK_MOD_USER_NAME FROM SYS_MOCK_INFO JOIN SYS_HANG_INFO USING (HANG_CODE) JOIN SYS_GWAN_INFO USING (GWAN_CODE) JOIN SYS_GHM_INFO USING (GHM_CODE)
AND MOCK_CODE = #{mock_code}
AND HANG_CODE = #{hang_code}
AND MOCK_NUMB = #{mock_numb}
AND GHM_CODE = #{ghm_code}
AND GHM_YEAR = #{ghm_year}
AND BSNS_CATE = #{bsns_cate}
AND GWAN_IO_TYPE = #{gwan_io_type}
AND GWAN_CODE = #{gwan_code}
AND GHM_ACTIVE_YN = #{ghm_active_yn}
ORDER BY GWAN_IO_TYPE, GWAN_NUMB, HANG_NUMB, MOCK_NUMB

mapper.insert:
INSERT INTO SYS_MOCK_INFO (
	HANG_CODE
	, MOCK_CODE
	, MOCK_NUMB
	, MOCK_NAME
	, MOCK_NOTE
	, MOCK_REG_DATE
	, MOCK_MOD_DATE
	, MOCK_MOD_USER
)
VALUES (
	#{hang_code}
	, #{mock_code}
	, #{mock_numb}
	, #{mock_name}
	, #{mock_note}
	, NOW()
	, NOW()
	, #{user_id}
)

mapper.update:
UPDATE SYS_MOCK_INFO
SET
	MOCK_NAME = #{mock_name}
	, MOCK_NOTE = #{mock_note}
	, MOCK_MOD_DATE = NOW()
	, MOCK_MOD_USER = #{user_id}
WHERE MOCK_CODE = #{mock_code}

