mapper.select:
SELECT COMP_CODE , USER_ID , USER_PW , USER_NAME , USER_EMAIL , USER_TEL , USER_MOBILE , USER_REG_NUMB_1 , USER_REG_NUMB_2 , CONCAT( USER_REG_NUMB_1, '-', USER_REG_NUMB_2 ) AS USER_REG_NUMB , USER_JOIN_DATE , USER_SALES , USER_LEVEL , USER_APPR_YN , USER_YN , DATE_FORMAT(USER_LOGIN_DATE, '%Y-%m-%d') AS USER_LOGIN_DATE , DATE_FORMAT(USER_REG_DATE, '%Y-%m-%d') AS USER_REG_DATE , DATE_FORMAT(USER_MOD_DATE, '%Y-%m-%d') AS USER_MOD_DATE , USER_MOD_USER FROM DF_USER_INFO
AND USER_ID = #{user_id}
AND COMP_CODE = #{comp_code}
AND USER_REG_NUMB_1 = #{user_reg_numb_1} AND USER_REG_NUMB_2 = #{user_reg_numb_2}

mapper.delete:
DELETE FROM DF_COMP_CEO
WHERE COMP_CODE = #{comp_code}
	AND USER_ID = #{user_id}

mapper.update:
UPDATE DF_USER_INFO
SET
	USER_APPR_YN = 'Y'
  , USER_MOD_DATE = NOW()
  , USER_MOD_USER = #{user_id}
WHERE COMP_CODE = #{comp_code}
	AND USER_ID = #{update_user_id}

mapper.select:
SELECT COMP_CODE , USER_ID , USER_NAME , USER_EMAIL , USER_TEL , USER_MOBILE , USER_REG_NUMB_1 , USER_REG_NUMB_2 , CONCAT( USER_REG_NUMB_1, '-', USER_REG_NUMB_2 ) AS USER_REG_NUMB , USER_JOIN_DATE , USER_SALES , (SELECT CDDT_NAME FROM SYS_CODE_DETAIL WHERE CODE_ID = 'USER_POSITION' AND CDDT_VAL = USER_POSITION ) AS USER_POSITION_NAME , USER_LEVEL , USER_APPR_YN , IF(USER_APPR_YN='Y', '승인', '미승인') AS USER_APPR_YN_NAME , USER_YN , IF(USER_YN='Y', '사용', '미사용') AS USER_YN_NAME , IFNULL(USER_LOGIN_DATE, '') AS USER_LOGIN_DATE , DATE_FORMAT(USER_REG_DATE, '%Y-%m-%d') AS USER_REG_DATE , DATE_FORMAT(USER_MOD_DATE, '%Y-%m-%d') AS USER_MOD_DATE , USER_MOD_USER FROM DF_USER_INFO DUI WHERE COMP_CODE = #{comp_code}
AND USER_APPR_YN = #{user_appr_yn}
AND USER_YN = #{user_yn}
AND ${keyword_column} LIKE CONCAT('%', #{keyword}, '%')

mapper.update:
UPDATE DF_USER_INFO
SET
	USER_NAME = #{user_name}
	, USER_EMAIL = #{user_email}
	, USER_TEL = #{user_tel}
	, USER_MOBILE = #{user_mobile}
	, USER_MOD_DATE = NOW()
	, USER_MOD_USER = #{user_id}
WHERE USER_ID = #{user_id}

UPDATE DF_USER_INFO
SET
	USER_PW = #{user_enc_pw}
WHERE USER_ID = #{user_id}

UPDATE DF_USER_INFO
SET
	USER_PW = #{user_pw}
  , USER_NAME = #{user_name}
  , USER_EMAIL = #{user_email}
  , USER_TEL = #{user_tel}
  , USER_MOBILE = #{user_mobile}
  , USER_REG_NUMB_1 = #{user_reg_numb_1}
  , USER_REG_NUMB_2 = #{user_reg_numb_2}
  , USER_JOIN_DATE = #{user_join_date}
  , USER_SALES = #{user_sales}
  , USER_POSITION = #{user_position}
  , USER_LEVEL = #{user_level}
  , USER_APPR_YN = #{user_appr_yn}
  , USER_YN = #{user_yn}
  , PASSWORD_HINT = #{password_hint}
  , PASSWORD_HINT_ANSWER = #{password_hint_answer}
  , USER_LOGIN_DATE = #{user_login_date}
  , USER_REG_DATE = #{user_reg_date}
  , USER_MOD_DATE = #{user_mod_date}
  , USER_MOD_USER = #{user_mod_user}
WHERE COMP_CODE = #{comp_code}
  AND USER_ID = #{user_id}

mapper.select:
SELECT USER_PW
FROM DF_USER_INFO
WHERE USER_ID = #{user_id}
  AND PASSWORD_HINT = #{password_hint}
  AND PASSWORD_HINT_ANSWER = #{password_hint_answer}
